import React, { Fragment, useState, useEffect } from "react";
import Make from "../components/Make/Make";
import useHttp from "../hooks/use-http";
import Loader from "../UI/Loader";
import axios from "axios";
const VehiclesMake = () => {
  const [makes, setMakes] = useState([]);
  const { isLoading, error, sendRequest: fetchMakes } = useHttp();

  // useEffect(() => {
  //   const transformedMakes = (makeObj) => {
  //     const loadedMakeList = [];

  //     for (const key in makeObj.Results) {
  //       loadedMakeList.push({
  //         id: key,
  //         Make_ID: makeObj.Results[key].Make_ID,
  //         Make_Name: makeObj.Results[key].Make_Name,
  //       });
  //     }
  //     setMakes(loadedMakeList);
  //   };
  //   fetchMakes(
  //     {
  //       url: "https://vpic.nhtsa.dot.gov/api/vehicles/GetAllMakes?format=json",
  //     },
  //     transformedMakes
  //   );
  // }, [fetchMakes]);
  const [vhdata, setVhdata] = useState([]);
  useEffect(() => {
    axios
      .get(
        "https://vpic.nhtsa.dot.gov/api/vehicles/GetAllMakes?format=json&Count=10"
      )
      .then(({ data }) => {
        const newVhdata = [];
        data.Results.forEach((p) => newVhdata.push(p.Make_Name, p.Make_ID));
        setVhdata(newVhdata);
      });
  });
  return (
    <Fragment>
      {!isLoading && makes.length > 0 && <Make items={makes} />}
      {isLoading && <Loader />}

      {vhdata.map((p, i) => {
        return <div key={i}>{p}</div>;
      })}
    </Fragment>
  );
};
export default VehiclesMake;
