import React, { Fragment, useState, useEffect } from "react";
import Make from "../components/Make/Make";
import useHttp from "../hooks/use-http";
import Loader from "../UI/Loader";
import axios from "axios";
const VehiclesMake = () => {
  const [makes, setMakes] = useState([]);
  const { isLoading } = useHttp();
  const [Error, SetError] = useState(false);
  const loadmorevehicledata = () => {
    axios
      .get(
        "https://vpic.nhtsa.dot.gov/api//vehicles/GetMakesForVehicleType/car?format=json"
      )
      .then(({ data }) => {
        const newVhdata = [];
        //console.log(data);
        //data.Results.forEach((p) => newVhdata.push(p.Make_Name));

        for (const key in data.Results) {
          newVhdata.push({
            MakeId: data.Results[key].MakeId,
            MakeName: data.Results[key].MakeName,
          });
        }
        setMakes(newVhdata);
      });
  };
  useEffect(() => {
    loadmorevehicledata();
  }, []);
  return (
    <Fragment>
      {!isLoading && makes.length > 0 && <Make items={makes} />}
      {isLoading && <Loader />}
    </Fragment>
  );
};
export default VehiclesMake;
