import React, { useState, useEffect } from "react";
import Make from "../components/Make/Make";
import useHttp from "../hooks/use-http";

const VehiclesMake = () => {
  const [makes, setMakes] = useState([]);
  //const { isLoading, error, sendRequest: fetchMakes } = useHttp();

  // useEffect(() => {
  //   const transformedMakes = (makeObj) => {
  //     const loadedMakeList = [];

  //     for (const key in makeObj) {
  //       loadedMakeList.push({
  //         id: key,
  //         Make_ID: makeObj[key].Make_ID,
  //         Make_Name: makeObj[key].Make_Name,
  //       });
  //     }
  //     setMakes(loadedMakeList);
  //   };
  //   fetchMakes(
  //     {
  //       url: "https://vpic.nhtsa.dot.gov/api/vehicles/GetAllMakes?format=json",
  //     },
  //     transformedMakes
  //   );
  // }, [fetchMakes]);
  useEffect(() => {
    const loadedMakeList = [];
    fetch(`https://vpic.nhtsa.dot.gov/api/vehicles/GetAllMakes?format=json`)
      .then((response) => {
        if (!response.ok) {
          throw new Error(
            `This is an HTTP error: The status is ${response.status}`
          );
        }
        return response.json();
      })
      .then((actualData) => {
        console.log(actualData);
        for (const key in actualData) {
          loadedMakeList.push({
            id: key,
            Make_ID: actualData[key].Make_ID,
            Make_Name: actualData[key].Make_Name,
          });
        }
        setMakes(loadedMakeList);
      })
      .catch((err) => {
        console.log(err.message);
      });
  }, []);
  return <Make items={makes} />;
};
export default VehiclesMake;
