import React, { Fragment, useState, useEffect } from "react";
import Make from "../components/Make/Make";
import useHttp from "../hooks/use-http";
import Loader from "../UI/Loader";
import axios from "axios";
const VehiclesMake = () => {
  const [makes, setMakes] = useState([]);
  const { isLoading, error, sendRequest: fetchMakes } = useHttp();

  const [vhdata, setVhdata] = useState([]);

  const loadmorevehicledata = () => {
    axios
      .get("https://vpic.nhtsa.dot.gov/api/vehicles/GetAllMakes?format=json")
      .then(({ data }) => {
        const newVhdata = [];
        data.Results.forEach((p) =>
          newVhdata.push({ name: p.Make_Name, id: p.Make_ID })
        );
        setMakes(newVhdata);
      });
  };
  useEffect(() => {
    loadmorevehicledata();
  }, []);
  return (
    <Fragment>
      {!isLoading && makes.length > 0 && <Make items={makes} />}
      {isLoading && <Loader />}
    </Fragment>
  );
};
export default VehiclesMake;
